package code.java.strings;

import java.util.ArrayList;
import java.util.List;
import java.util.function.BiConsumer;

/**
 * https://leetcode.com/problems/find-all-anagrams-in-a-string/
 */
public class FindAllAnagramsInString {

    private static List<Integer> findAnagramsInitialTry(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (s.length() < p.length()) return result;
        //
        int[] store = new int[26];
        for (char c : p.toCharArray()) {
            store[c - 'a']++;
        }
        int i = 0, j = 0;
        int[] chars = new int[26]; // To be checked carefully
        while (j < s.length()) {
            if (store[s.charAt(j) - 'a'] == 0) {
                removeChars(chars);
                i = j = j + 1;
            } else {
                while (j - i < p.length() && j < s.length() && store[s.charAt(j) - 'a'] != 0) {
                    chars[s.charAt(j++) - 'a']++;
                }
                // Range is equal
                if (areSubStringsEqual(store, chars)) {
                    result.add(i);
                }
                chars[s.charAt(i) - 'a']--;
                i = i + 1;
            }
        }
        return result;
    }

    private static void removeChars(int[] chars) {
        int idx = 0;
        while (idx < 26) {
            chars[idx++] = 0;
        }
    }

    private static boolean areSubStringsEqual(int[] store, int[] chars) {
        for (int i = 0; i < 26; i++) {
            if (store[i] != chars[i]) return false;
        }
        return true;
    }

    private static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (s.length() < p.length()) return result;
        //
        int[] store = new int[26];
        for (char c : p.toCharArray()) {
            store[c - 'a']++;
        }
        int i = 0, j = 0, window = p.length();
        while (j < s.length()) {
            // Reduce the char occurrences and search space window
            if (store[s.charAt(j++) - 'a']-- > 0) window--;
            // Add the index to the result;
            if (window == 0) result.add(i);
            // Increase window size and char store previous state
            if (j - i == p.length() && store[s.charAt(i++) - 'a']++ >= 0) window++;
        }
        return result;
    }


    public static void main(String[] args) {
        BiConsumer<String[], List<Integer>> logger = (str, result) -> {
            System.out.println("S - " + str[0] + ", P - " + str[1] + ", Result - " + result);
        };
        //
        String s = "cbaebabacd", p = "abc";
        List<Integer> result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
        //
        s = "abab"; p = "ab";
        result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
        //
        s = "aa"; p = "bb";
        result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
        //
        s = "";
        p = "z";
        result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
        //
        s = "eklpyqrbgjdwtcaxzsnifvhmoueklpyqrbgjdwtcaxzsnifvhmoueklpyqrbgjdwtcaxzsnifvhmouek";
        p = "yqrbgjdwtcaxzsnifvhmou";
        result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
        //
        s = "";
        p = "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
        result = findAnagrams(s, p);
        logger.accept(new String[]{s, p}, result);
    }

}
